Hyper-cube path```#include<bits/stdc++.h>
using namespace std;
#define N 1000000
int c[N],w[N];
int dp(int goal,int n){
    if(c[goal]>=0) return c[goal];
    int imax=0;
    for(int i=0;i<n;i++){//從0位到n-1位
        if(goal & (1<<i)){//如果第i位是1
            int k=dp(goal^(1<<i),n);//把第i位變成0
            if(k>imax) imax=k;
        }
    }
    c[goal]=imax+w[goal];
    return c[goal];
}
