        p[r1]=r2;
        mst.insert(-p[r2]);
        maxn=max(maxn,-p[r2]);
    }else{
        p[r1]+=p[r2];
        p[r2]=r1;
        mst.insert(-p[r1]);
        maxn=max(maxn,-p[r1]);
    }
}
